import { useState } from "react";
import { createTransaction, fetchCategories } from "../services/api";
import { useEffect } from 'react'; 

const AddTransaction = ({ onTransactionAdded }) => {
    const [title, setTitle] = useState('');
    const [amount, setAmount] = useState('');
    const [date, setDate] = useState('');
    const [category, setCategory] = useState('');
    const [categories, setCategories] = useState([]);
    const [error, setError] = useState(null);


    useEffect(() => {
        const fetchData = async () => {
            try {
                const result = await fetchCategories();
                setCategories(result);
            } catch (err) {
                setError('Failed to fetch categories');
            }
        };
        fetchData();
    }, []);







    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            let categoryId = categories.find((c) => c.name === category)?.id;

            if (!categoryId) {

                const newCategory = await fetch(`/api/Category`, {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ name: category }),
                });
                const newCategoryData = await newCategory.json();
                categoryId = newCategoryData.id;

            }
            const transaction = {
                title,
                amount: parseFloat(amount),
                date,
                categoryId,
            };
            await createTransaction(transaction);
            setTitle('');
            setAmount('');
            setDate('');
            setCategory('');
            if (onTransactionAdded) {
                onTransactionAdded(transaction);
            }
        } catch (err) {
            setError('Failed to add transaction');
        }
    };
    return (
        <form onSubmit={handleSubmit}>
            <h2> Add Transaction</h2>
            {error && <div>{error}</div>}
            <div>
                <label>Title:</label>
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
            </div>
            <div>
                <label>Amount:</label>
                <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} required />
            </div>
            <div>
                <label>Date:</label>
                <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
            </div>
            <div>
                <label>Category:</label>
                <select value={category} onChange={(e) => setCategory(e.target.value)}>
                    <option value=""> --Select category---</option>
                    {categories.map((c) => (
                        <option key={c.id} value={c.name}>
                            {c.name}
                        </option>
                    ))}
                </select>
                <input 
                    type="text"
                    placeholder="Or enter a new category"
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                    />

            </div>

            <div>
                <button type="submit">Add Transaction</button>
            </div>
        </form>
    );

};

export default AddTransaction;
