import './Layout.css';
import CategorySummary from './components/CategorySummary';
import TransactionList from './components/TransactionList';
import AddTransaction from './components/AddTransaction';
import Analytics from './components/Analytics';
import { useEffect, useState } from 'react';
import { fetchTransactions, fetchCategories } from './services/api';

function App() {
    const [transactions, setTransactions] = useState([]);
    const [categories, setCategories] = useState([]);
    const [refreshKey, setRefreshKey] = useState(0);

    const fetchAllTransactions = async () => {
        const data = await fetchTransactions();
        setTransactions(data);
    };

    const fetchAllCategories = async () => {
        const data = await fetchCategories();
        setCategories(data);
    };

    const handleTransactionAdded = () => {
        setRefreshKey((prevKey) => prevKey + 1);
        fetchAllTransactions();
        fetchAllCategories(); // Synka kategorier efter att en ny kategori/transaktion l�ggs till
    };

    useEffect(() => {
        fetchAllTransactions();
        fetchAllCategories(); // H�mta kategorier vid start
    }, []);

    return (
        <div className="App">
            <header>
                <h1>Svedbucks</h1>
            </header>
            <main>
                <div className="flex-container">
                    <div className="column">
                        <CategorySummary categories={categories} />
                    </div>
                    <div className="column">
                        <AddTransaction onTransactionAdded={handleTransactionAdded} categories={categories} />
                    </div>
                </div>
                <div className="section">
                    <TransactionList transactions={transactions} />
                </div>
                <div className="section">
                    <Analytics refreshKey={refreshKey} />
                </div>
            </main>
        </div>
    );
}

export default App;
