import { useState, useEffect } from "react";
import { createTransaction, fetchCategories } from "../services/api";
import { apiClient } from "../services/api";

const AddTransaction = ({ onTransactionAdded, categories }) => {
    const [title, setTitle] = useState('');
    const [amount, setAmount] = useState('');
    const [date, setDate] = useState('');
    const [category, setCategory] = useState('');
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            let categoryId = categories.find((c) => c.name === category)?.id;

            if (!categoryId && category.trim() !== "") {
                const newCategory = await apiClient.post('/Category', { name: category });
                categoryId = newCategory.data.id;
            }

            if (!categoryId) {
                throw new Error("Category is required.");
            }

            const transaction = {
                title,
                amount: parseFloat(amount),
                date,
                categoryId,
            };

            const newTransaction = await createTransaction(transaction);

            setTitle('');
            setAmount('');
            setDate('');
            setCategory('');
            if (onTransactionAdded) {
                onTransactionAdded(newTransaction);
            }
        } catch (err) {
            setError('Failed to add transaction');
        }
    };

    return (
        <form onSubmit={handleSubmit} className="add-transaction-form">
            <h2>Add Transaction</h2>
            {error && <div className="error-message">{error}</div>}
            <div className="form-group">
                <label>Title:</label>
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label>Amount:</label>
                <input
                    type="number"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label>Date:</label>
                <input
                    type="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label>Category:</label>
                <select value={category} onChange={(e) => setCategory(e.target.value)}>
                    <option value="">--Select category--</option>
                    {categories.map((c) => (
                        <option key={c.id} value={c.name}>
                            {c.name}
                        </option>
                    ))}
                </select>
                <input
                    type="text"
                    placeholder="Or enter a new category"
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                />
            </div>
            <button type="submit" className="submit-button">Add Transaction</button>
        </form>
    );
};

export default AddTransaction;
