import React, { useState } from 'react';
import '../Layout.css';

const TransactionList = ({ transactions, onEdit }) => {
    const [editingTransaction, setEditingTransaction] = useState(null);

    const handleEdit = (transaction) => {
        setEditingTransaction(transaction);
        if (onEdit) {
            onEdit(transaction);
        }
    };

    return (
        <div>
            <h2>Transaction History</h2>
            <div className="transaction-history">
                <ul>
                    {transactions
                        .slice() // Skapa en kopia f�r att inte mutera original-arrayen
                        .sort((a, b) => new Date(b.date) - new Date(a.date)) // Sortera efter datum (senaste f�rst)
                        .map((transaction) => (
                            <li key={transaction.id} className="transaction-row">
                                <div className="transaction-details">
                                    <span className="transaction-title">{transaction.title}</span>
                                    <span
                                        className={`transaction-amount ${transaction.amount >= 0 ? 'positive' : ''
                                            }`}
                                    >
                                        {transaction.amount} kr
                                    </span>
                                    <span className="transaction-date">
                                        {new Date(transaction.date).toLocaleDateString()}
                                    </span>
                                </div>
                                <div className="transaction-actions">
                                    <button onClick={() => console.log('Edit', transaction)}>Edit</button>
                                </div>
                            </li>
                        ))}
                </ul>
            </div>
        </div>
    );
};

export default TransactionList;
