import React, { useEffect, useState } from 'react'; // Beh�ll useEffect och useState om de anv�nds internt
import { fetchTransactions, updateTransaction } from '../services/api'; // Beh�ll befintliga API-funktioner
import '../Layout.css';

const TransactionList = ({ categoryId }) => {
    const [transactions, setTransactions] = useState([]); // Hanterar transaktionsdata
    const [error, setError] = useState(null); // Hanterar felmeddelanden
    const [editingTransaction, setEditingTransaction] = useState(null); // Hanterar vilken transaktion som redigeras

    const handleEdit = (transaction) => {
        setEditingTransaction(transaction); // S�tt vald transaktion f�r redigering
    };

    const fetchData = async () => {
        try {
            const result = await fetchTransactions(categoryId); // H�mta transaktioner baserat p� kategori
            const sortedTransactions = result.sort((a, b) => new Date(b.date) - new Date(a.date)); // Sortera efter datum
            setTransactions(sortedTransactions); // Uppdatera transaktionslistan
        } catch (err) {
            setError('Failed to fetch transactions'); // Visa felmeddelande om API-anrop misslyckas
        }
    };

    useEffect(() => {
        if (categoryId) {
            fetchData(); // H�mta data n�r komponenten laddas eller kategori �ndras
        }
    }, [categoryId]);

    if (error) {
        return <div>{error}</div>; // Visa fel om det finns ett
    }

    return (
        <div>
            <h2>Transaction History</h2>
            <div className="transaction-history">
                <ul>
                    {transactions.map((transaction) => (
                        <li key={transaction.id} className="transaction-row">
                            <div className="transaction-details">
                                <span className="transaction-title">{transaction.title}</span>
                                <span
                                    className={`transaction-amount ${transaction.amount >= 0 ? 'positive' : ''
                                        }`}
                                >
                                    {transaction.amount} kr
                                </span>
                                <span className="transaction-date">
                                    {new Date(transaction.date).toLocaleDateString()}
                                </span>
                            </div>
                            <div className="transaction-actions">
                                <button onClick={() => handleEdit(transaction)}>Edit</button>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
            {editingTransaction && (
                <form
                    onSubmit={async (e) => {
                        e.preventDefault(); // F�rhindra sidladdning
                        try {
                            await updateTransaction(editingTransaction); // Uppdatera backend
                            setEditingTransaction(null); // Avsluta redigering
                            fetchData(); // H�mta uppdaterad lista
                        } catch (err) {
                            console.error('Failed to update transaction:', err); // Logga fel
                        }
                    }}
                >
                    <h3>Edit Transaction</h3>
                    <input
                        type="text"
                        value={editingTransaction.title}
                        onChange={(e) =>
                            setEditingTransaction({ ...editingTransaction, title: e.target.value })
                        }
                    />
                    <input
                        type="number"
                        value={editingTransaction.amount}
                        onChange={(e) =>
                            setEditingTransaction({
                                ...editingTransaction,
                                amount: parseFloat(e.target.value),
                            })
                        }
                    />
                    <button type="submit">Save</button>
                    <button onClick={() => setEditingTransaction(null)}>Cancel</button>
                </form>
            )}
        </div>
    );
};

export default TransactionList;
