import React, { useEffect, useState } from 'react'; // Korrigerad import
import { fetchTransactions } from '../services/api';
import '../layout.css';

const TransactionList = ({ categoryId }) => {
    const [transactions, setTransactions] = useState([]);
    const [error, setError] = useState(null);
    const [editingTransaction, setEditingTransaction] = useState(null); // Deklarera state

    const handleEdit = (transaction) => {
        setEditingTransaction(transaction);
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const result = await fetchTransactions(categoryId);
                setTransactions(result);
            } catch (err) {
                setError('Failed to fetch transactions');
            }
        };

        if (categoryId) {
            fetchData();
        }
    }, [categoryId]);

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2>Transaction History</h2>
            <div className="transaction-history">
                <ul>
                    {transactions.map((transaction) => (
                        <li key={transaction.id}>
                            <span className="transaction-title">{transaction.title}</span>
                            <span
                                className={`transaction-amount ${transaction.amount >= 0 ? 'positive' : ''
                                    }`}
                            >
                                {transaction.amount} kr
                            </span>
                            <span className="transaction-date">
                                {new Date(transaction.date).toLocaleDateString()}
                            </span>
                            <button onClick={() => handleEdit(transaction)}>Edit</button>
                        </li>
                    ))}
                </ul>
            </div>
            {editingTransaction && (
                <form
                    onSubmit={async (e) => {
                        e.preventDefault();
                        try {
                            console.log('Updating transaction:', editingTransaction);
                            setEditingTransaction(null);
                        } catch (err) {
                            console.error('Failed to update transaction');
                        }
                    }}
                >
                    <h3>Edit Transaction</h3>
                    <input
                        type="text"
                        value={editingTransaction.title}
                        onChange={(e) =>
                            setEditingTransaction({ ...editingTransaction, title: e.target.value })
                        }
                    />
                    <input
                        type="number"
                        value={editingTransaction.amount}
                        onChange={(e) =>
                            setEditingTransaction({
                                ...editingTransaction,
                                amount: parseFloat(e.target.value),
                            })
                        }
                    />
                    <button type="submit">Save</button>
                    <button onClick={() => setEditingTransaction(null)}>Cancel</button>
                </form>
            )}
        </div>
    );
};

export default TransactionList;
